
"use client";

import { useCallback, useEffect, useRef } from 'react';
import { Task } from '@/lib/types';

const notificationTimeouts = new Map<string, NodeJS.Timeout>();

// A simple, short, and royalty-free alarm sound encoded in Base64
const ALARM_SOUND_DATA_URI = "data:audio/mp3;base64,-";

const playAlarmSound = () => {
    try {
        const audio = new Audio(ALARM_SOUND_DATA_URI);
        audio.play().catch(e => console.error("Could not play alarm sound:", e));
    } catch (error) {
        console.error("Failed to create audio element for alarm:", error);
    }
};

export function useNotifications() {
  const permissionRef = useRef<NotificationPermission>();

  useEffect(() => {
    if ("Notification" in window) {
      permissionRef.current = Notification.permission;
    }
  }, []);

  const scheduleNotification = useCallback((task: Task) => {
    if (permissionRef.current !== 'granted' || !task.date || !task.time) {
      return;
    }

    if (notificationTimeouts.has(task.id)) {
      clearTimeout(notificationTimeouts.get(task.id));
      notificationTimeouts.delete(task.id);
    }

    const [hours, minutes] = task.time.split(':').map(Number);
    const notificationDate = new Date(task.date);
    notificationDate.setHours(hours, minutes, 0, 0);

    const now = new Date();
    const timeUntilNotification = notificationDate.getTime() - now.getTime();

    if (timeUntilNotification > 0) {
      const timeoutId = setTimeout(() => {
        new Notification(task.text, {
          body: task.description || 'Your task is due now.',
          icon: '/logo.png',
        });
        playAlarmSound();
        notificationTimeouts.delete(task.id);
      }, timeUntilNotification);

      notificationTimeouts.set(task.id, timeoutId);
    }
  }, []);

  const cancelNotification = useCallback((taskId: string) => {
    if (notificationTimeouts.has(taskId)) {
      clearTimeout(notificationTimeouts.get(taskId));
      notificationTimeouts.delete(taskId);
    }
  }, []);
  
  return { scheduleNotification, cancelNotification };
}

    